const Client = require('azure-iothub').Client
/**
 * More documentation on sending data to an Azure connected device can be found here:
 * https://docs.microsoft.com/en-us/azure/iot-hub/quickstart-control-device-node
 */
const { deviceId, methodName, payload, responseTimeoutInSeconds = 10, CONNECTION_STRING } = options.requiredProperties

async function main () {
  if (!deviceId || !methodName || !CONNECTION_STRING) {
    return send(new Error('REQUIRED: deviceId, methodName'))
  }

  const client = Client.fromConnectionString(CONNECTION_STRING)

  const result = await client.invokeDeviceMethod(deviceId, { methodName, payload, responseTimeoutInSeconds })
  send(null, { observedState: 'Success', rawData: { response: result } })
}

main().catch(error => {
  console.error(error)
  send(null, {
    observedState: 'Error',
    rawData: { errorMessage: 'Failed to invoke method \'' + methodName + '\': ' + error.message }
  })
})
