const SftpClient = require('ssh2-sftp-client')

const {
  FTP_USER: username,
  FTP_PASSWORD: password,
  FTP_HOST: host,
  FTP_PORT: port,
  FTP_FILEPATH: path,
  filename,
  data
} = options.requiredProperties

async function execute () {
  if (!data || !username || !password || !host || !port || !path || !filename) {
    return send(new Error('Missing required property'))
  }

  const buffer = Buffer.from(data)
  const client = new SftpClient()

  try {
    await client.connect({
      host,
      port,
      username,
      password
    })
  } catch (error) {
    console.error(error)
    return send(null, { observedState: 'Error', rawData: { errorMessage: 'Connection error: ' + error } })
  }

  try {
    const resp = await client.put(buffer, `${path}${filename}`)
    send(null, { observedState: 'Success', rawData: { response: resp } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'File upload error: ' + error } })
  }
}

execute().catch(send)
