const AWS = require('aws-sdk')

function reportResults (error, data, send) {
  if (error) {
    console.log('Cannot invoke endpoint')
    console.log(error, error.stack)
    return send(new Error(error))
  }

  // Parse the returned data
  const responseData = Buffer.from(data.Body).toString('utf8')

  const result = {
    observedState: 'Predicted',
    rawData: {
      data: responseData
    }
  }

  return send(null, result)
}

function invokeSageMaker (error, options, send) {
  if (error) {
    console.log('Cannot assume role')
    console.log(error, error.stack)
    return send(new Error(error))
  }

  const {
    endpoint,
    contentType,
    observations
  } = options.requiredProperties

  const sageMakerRuntime = new AWS.SageMakerRuntime({})

  sageMakerRuntime.invokeEndpoint({
    Body: JSON.stringify(observations),
    EndpointName: endpoint,
    ContentType: contentType
  },
  (error, data) => reportResults(error, data, send)
  )
}

function updateAwsConfig (awsConfig) {
  AWS.config.update({
    credentials: {
      accessKeyId: awsConfig.key,
      secretAccessKey: awsConfig.secret
    },
    region: awsConfig.region
  })
}

function checkSensorPrerequisites (options, send) {
  // Check if everything is provided, else fail early
  if (!options.requiredProperties.endpoint) { return send(new Error('Missing property: endpoint')) }
  if (!options.requiredProperties.observations) { return send(new Error('Missing property: observations')) }
  if (!options.requiredProperties.contentType) { return send(new Error('Missing property: contentType')) }
  if (!options.requiredProperties.SAGEMAKER_CONFIG) { return send(new Error('Missing SageMaker config.')) }

  return true
}

function execute () {
  if (!checkSensorPrerequisites(options, send)) return

  const awsConfig = JSON.parse(options.requiredProperties.SAGEMAKER_CONFIG)
  updateAwsConfig(awsConfig)

  // Switch to the role that is allowed to invoke sagemaker endpoint.
  const sts = new AWS.STS()
  sts.assumeRole({
    RoleArn: awsConfig.role,
    RoleSessionName: 'sagemaker-session'
  },
  error => invokeSageMaker(error, options, send)
  )
}

execute()
