const Twilio = require('twilio')

const { TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN, message, to, from } = options.requiredProperties

async function execute () {
  if (!message || !TWILIO_ACCOUNT_SID || !TWILIO_AUTH_TOKEN || !to || !from) {
    return send(new Error('Missing property'))
  }

  try {
    const client = new Twilio(TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN)
    const call = await client.calls.create({
      to: to,
      from: from,
      twiml: '<Response><Say voice="woman" loop="2">' + message + '</Say></Response>'
    })

    console.info('Success! Call is being made:')
    console.info(call.sid)

    send(null, { observedState: 'Success', rawData: { response: call } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Call failed: ' + error } })
  }
}

execute()
