script:
const { type, resource } = options.requiredProperties

async function execute () {
  if (!type) {
    return send(new Error('Missing property type'))
  }

  if (!resource) {
    return send(new Error('resource not defined'))
  }

  const alarmFilter = {
    source: resource,
    status: ['ACTIVE', 'ACKNOWLEDGED'],
    all: true,
    type
  }

  try {
    const searchResult = await waylay.alarms.search(alarmFilter)
    console.log(`Found ${searchResult.alarms.length} alarms to clear`)

    const response = await Promise.all(searchResult.alarms.map((alarm) => {
      return waylay.alarms.update(alarm.id, { status: 'CLEARED' })
    }))
    send(null, { observedState: 'Success', rawData: { response } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Failed to clear alarms: ' + error } })
  }
}

execute()
