const { propertyList, sensor } = options.requiredProperties

async function execute () {
  if (!propertyList || !sensor) {
    return send(new Error('Missing property'))
  }

  let parsedProperties
  try {
    parsedProperties = typeof propertyList === 'string' ? JSON.parse(propertyList) : propertyList
  } catch (error) {
    return send(error)
  }
  try {
    const results = parsedProperties.map((properties) => {
      return waylay.sensors.execute(sensor, 'latest', { properties })
    })
    const response = await Promise.all(results)

    send(null, { observedState: 'Success', rawData: { response } })
  } catch (error) {
    console.error(error)
    send(null, {
      observedState: 'Error',
      rawData: { errorMessage: 'An error occured while executing sensor: ' + error }
    })
  }
}

execute()
