const moment = require('moment')

async function execute () {
  const now = moment()
  return send(null, {
    observedState: 'Data',
    rawData: {
      timestamp: now.valueOf(),
      year: now.year(),
      month: now.month() + 1,
      day: now.date(),
      hours: now.hour(),
      minutes: now.minute(),
      seconds: now.second(),
      dateString: now.toString(),
      dayString: now.format('dddd'),
      ISOstring: now.toISOString()
    }
  })
}

execute()
