const moment = require('moment')

function parseISOStringDataInput (input) {
  const duration = moment.duration(input)
  return moment().subtract(duration).unix() * 1000
}

function parseTimestamp (timestamp) {
  if (!isNaN(timestamp)) { return timestamp }
  if (timestamp && timestamp !== '' && isNaN(timestamp)) { return parseISOStringDataInput(timestamp) }
}

function createQueryOptions (window, from, until, columnwise) {
  const queryOptions = {}

  if (window) queryOptions.window = window
  if (from) queryOptions.from = from
  if (until) queryOptions.until = until
  queryOptions['render.mode'] = columnwise ? 'RENDER_MODE_SERIES' : 'RENDER_MODE_COMPACT_WS'

  return queryOptions
}

function hasData (response) {
  return response && response.data && response.data[0].columns && response.data[0].columns.length > 1
}

function processResponse (response, removeTimestamp, columnwise) {
  const result = response.data[0]

  if (removeTimestamp) {
    result.columns.shift()

    if (columnwise) { result.data.shift() } else { result.data = result.data.map(row => { row.shift(); return row }) }
  }

  if (columnwise) {
    const nestedData = []
    nestedData.push(result.data)
    result.data = nestedData
  }

  return result
}

async function execute () {
  const { queryName, window, from, until } = options.requiredProperties.queryName

  const removeTimestamp = options.requiredProperties.removeTimestamp === 'true'
  const columnwise = options.requiredProperties.columnwise === 'true'

  const parsedFrom = parseTimestamp(from)
  const parsedUntil = parseTimestamp(until)

  if (!queryName) { return send(new Error('No valid queryName supplied.')) }
  if (window && parsedFrom && parsedUntil) { return send(new Error('Only specify 2 out of the window, from and until properties.')) }

  const queryOptions = createQueryOptions(window, parsedFrom, parsedUntil, columnwise)

  const response = await waylay.analytics.getQueryData(queryName, queryOptions)
    .catch(error => {
      send(error)
    })

  const result = hasData(response) && processResponse(response, removeTimestamp, columnwise)

  send(null, {
    observedState: result ? 'Collected' : 'Not Collected',
    rawData: result || response
  })
}

execute()
