const jwtDecode = require('jwt-decode')
const { tenant: tenantId } = jwtDecode(options.globalSettings.waylayToken)
const { name, json, version = 'latest' } = options.requiredProperties

async function execute () {
  let parsedJSON
  if (!name) {
    return send(new Error('Missing property'))
  }
  if (json && typeof json === 'string') {
    try {
      parsedJSON = JSON.parse(json)
    } catch (err) {
      return send(new Error('Error parsing JSON: ' + err))
    }
  }
  try {
    const response = await waylay.webscripts.call(tenantId, name, version, parsedJSON)
    send(null, { observedState: 'Success', rawData: { response } })
  } catch (error) {
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Failed to execute webscript: ' + error } })
  }
}

execute()
