async function main () {
  const { payload, resource } = options.requiredProperties
  const body = parsePayload(payload)

  if (!resource) {
    return send(new Error('No resource provided'))
  }

  await waylay.data.postSeries(resource, body, { forward: true, store: false })

  send(null, { observedState: 'Success' })
}

function parsePayload (payload) {
  if (!payload) {
    return send(new Error('No payload provided'))
  }

  try {
    payload = JSON.parse(payload)
  } catch (_) {
    console.log(payload)
    return send(new Error('payload has to be a valid JSON object'))
  }

  return payload
}

main().catch((error) => {
  send(null, { observedState: 'Error', rawData: { errorMessage: error.message } })
})
