const jsforce = require('jsforce')
const pify = require('pify')
const Waylay = require('@waylay/client')

async function main () {
  const Name = options.requiredProperties.name || options.node.RESOURCE

  const conn = await getSalesforceConnection()

  const SfAccount = pify(conn.sobject('Account'))

  const res = await SfAccount.find({ Name })

  if (res.length > 0) {
    send(null, { observedState: 'Found', rawData: res[0] })
  } else {
    send(null, { observedState: 'Not Found' })
  }
}

async function getSalesforceConnection () {
  try {
    const waylayClient = new Waylay({
      token: options.globalSettings.waylayToken
    })

    const { access_token: accessToken } = await waylayClient.hoard.getTokens('salesforce', { scope: 'organisation' })

    if (!accessToken) {
      throw new Error('No user or token provided via Hoard service')
    }

    const jsclient = new jsforce.Connection({
      instanceUrl: 'https://eu26.salesforce.com',
      accessToken
    })

    return jsclient
  } catch (error) {
    throw new Error(`error: ${error} \n possible solution: Connect Salesforce through the connections`)
  }
}

main().catch(send)
