script:
const { resource } = options.requiredProperties

if (!resource) {
  send(new Error('resource not defined'))
}

async function execute () {
  try {
    const response = await waylay.resources.children(resource)
    const rawData = Object.assign({}, response.values, { childIds: response.values.map(val => val.id) })
    send(null, { observedState: 'Found', rawData })
  } catch (error) {
    if (error.response && error.response.status === 404) {
      return send(null, {
        observedState: 'Not Found'
      })
    }

    return send(error)
  }
}

execute()
