script:
const resource = options.node.RESOURCE

if (resource === undefined || resource === '') {
  send(new Error('resource not defined'))
}

waylay.resources.children(resource)
  .then(response => {
    const rawData = Object.assign({}, response.values, { childIds: response.values.map(val => val.id) })
    send(null, { observedState: 'Collected', rawData })
  })
  .catch((error) => {
    send(null, { observedState: 'Not Collected', rawData : { error }})
  })
