const axios = require('axios')

const { OPENWEATHER_KEY: API_KEY, city } = options.requiredProperties

async function execute () {
  if (!API_KEY || !city) {
    return send(new Error('Required properties missing'))
  }

  try {
    const url = 'http://api.openweathermap.org/data/2.5/weather?q=' + city + '&units=metric' + '&appid=' + API_KEY
    const response = await axios.get(url)
    const { data } = response
    if (data.cod && data.cod !== '200' && data.message) {
      throw new Error(data.message)
    }
    const state = data.weather[0].main
    const value = {
      observedState: 'Collected',
      rawData: {
        temperature: data.main.temp,
        temp_feel: data.main.feels_like,
        pressure: data.main.pressure,
        humidity: data.main.humidity,
        temp_min: data.main.temp_min,
        temp_max: data.main.temp_max,
        wind_speed: data.wind.speed,
        wind_direction: data.wind.deg,
        clouds_coverage: data.clouds.all,
        sunrise: data.sys.sunrise,
        sunset: data.sys.sunset,
        longitude: data.coord.lon,
        latitude: data.coord.lat,
        name: data.name,
        condition: state,
        icon: 'http://openweathermap.org/img/w/' + data.weather[0].icon + '.png'
      }
    }

    send(null, value)
  } catch (error) {
    send(new Error('Failed to fetch current weather' + error))
  }
}

execute()
