script:
const { resource } = options.requiredProperties

if (!resource) {
  send(new Error('Resource not defined'))
}

async function execute () {
  try {
    const response = await waylay.resources.get(resource)
    send(null, { observedState: 'Found', rawData: { metadata: response } })
  } catch (error) {
    if (error.response && error.response.status === 404) {
      return send(null, {
        observedState: 'Not Found'
      })
    }
    console.error(error)
    send(error)
  }
}

execute()
