script:
const resource = options.node.RESOURCE

if(!resource){
    send(new Error('Resource not defined'))
}

waylay.resources.get(resource)
  .then(response => {
    send(null, { observedState: 'Collected', rawData: { metadata: response } })
  })
  .catch(() => {
    send(null, { observedState: 'Not Collected' })
  })
