const moment = require('moment')
const { resource, metric } = options.requiredProperties

function parseISOStringDataInput (input) {
  const duration = moment.duration(input)
  return moment().subtract(duration).unix() * 1000
}

function parseTimestamp (timestamp) {
  if (!isNaN(timestamp)) { return timestamp }
  if (timestamp && timestamp !== '' && isNaN(timestamp)) { return parseISOStringDataInput(timestamp) }
}

function createQueryParams (options) {
  let {
    from,
    to,
    grouping,
    aggregate
  } = options.requiredProperties

  const queryParams = {}

  if (from) queryParams.from = parseTimestamp(from)
  if (to) queryParams.until = parseTimestamp(to)

  if ((from === null || from === '') && (aggregate === null || aggregate === '')) {
    queryParams.from = new Date() - 60 * 60 * 1000 // One hour ago
    aggregate = 'mean'
  }

  if (grouping && grouping !== '') queryParams.grouping = grouping
  if (aggregate && aggregate !== '') queryParams.aggregate = aggregate

  return queryParams
}

async function execute () {
  if (!resource) {
    return send(new Error('Resource not defined'))
  }
  if (!metric) {
    return send(new Error('Please specify metric'))
  }

  const queryParams = createQueryParams(options)
  console.log('qs', queryParams)

  const response = await waylay.data.getMetricSeries(resource, metric, queryParams)
    .catch(error => {
      console.error(error)
      send(new Error('Error getting data: ' + error))
    })

  response.series = response.series.filter(d => d[1] !== null)
  send(null, {
    observedState: response.series.length > 0 ? 'Collected' : 'Not Collected',
    rawData: response
  })
}

execute()
