const { DateTime } = require('luxon')
const { get } = require('lodash')

const { timeZone = 'UTC', service, metricName } = options.requiredProperties

const until = DateTime.fromObject({ zone: timeZone })
const from = until.startOf('month')

async function fetchResourceCount () {
  if (metricName !== 'count') {
    return send(new Error(`Metric ${metricName} not found for resources`))
  }

  const resources = await waylay.resources.search({ limit: 0 })
  send(null, { observedState: 'Collected', rawData: { consumption: resources.total } })
}

async function execute () {
  if (!until.zone) {
    return send(new Error('Unknown timezone: ' + timeZone))
  }
  console.log('Getting metrics for ' + service + ' from ' + from.toString() + ' until ' + until.toString())

  if (service === 'resources') {
    return await fetchResourceCount()
  }

  const stats = await waylay.stats.getMetric(service, { from: from.toMillis(), until: until.toMillis() })
  const metricPath = `results.${metricName}.perMonth.aggregated`

  if (!metricPath) {
    return send(new Error(`Metric ${metricName} not found for ${service}`))
  }

  const consumption = get(stats, metricPath)

  send(null, { observedState: 'Collected', rawData: { consumption } })
}

execute().catch(send)
