const moment = require('moment')

async function execute () {
  const { resource, metric, since } = options.requiredProperties

  if (!resource) {
    throw new Error('No resource provided.')
  }
  const sinceString = String(since)
  let duration = sinceString.indexOf('P') > -1
    ? Date.now() - moment.duration(sinceString)
    : Number(moment(Number(sinceString)))

  duration = moment(duration)

  if (!moment(duration).isValid() || duration < 1000) {
    throw new Error(`Invalid property 'since' provided. (${sinceString})`)
  }

  const res = metric
    ? await waylay.data.getLastMetric(resource, metric)
    : await waylay.data.getLatestDocument(resource)
  const lastTimestamp = res.timestamp
  const hasDataSince = lastTimestamp > Number(duration)

  const observedState = hasDataSince ? 'True' : 'False'

  console.log(res)
  send(null, { observedState, rawData: { lastTimestamp } })
}

execute().catch(send)
