script:
const value = parseFloat(options.requiredProperties.value)
const lowerLimit = parseFloat(options.requiredProperties.lowerLimit)
const upperLimit = parseFloat(options.requiredProperties.upperLimit)

if ([value, lowerLimit, upperLimit].some(x => x == null)) {
  send(new Error('All properties should be defined'))
}
if (upperLimit <= lowerLimit) {
  send(new Error('Property "upperLimit" should be higher than "lowerLimit"'))
}

let state = 'In Range'
if (value < lowerLimit) {
  state = 'Below'
}
if (value > upperLimit) {
  state = 'Above'
}

send(null, { observedState: state })
