const axios = require('axios')

const { JIRA_USER: user, JIRA_PASSWORD: password, JIRA_URL: url, subject, message, project, type = 'Bug' } = options.requiredProperties

async function execute () {
  try {
    const data = {
      fields: {
        project: {
          key: project
        },
        summary: subject,
        description: message,
        issuetype: {
          name: type
        }
      }
    }
    const response = await axios.post(url, data, {
      auth: {
        user: user,
        password: password
      }
    })
    send(null, { observedState: 'Success', rawData: { response } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Failed to create jira ticket: ' + error } })
  }
}

execute()
