/*****************************************************************************
 EXAMPLE INPUT

 const geofences = [
 {
        "id": "Antwerp",
        "type": "circle",
        "radius": 10000,
        "coordinates": { "latitude": 51.21989, "longitude": 4.4025 }
    },
 {
        "id": "Ghent",
        "type": "polygon",
        "coordinates": [
            { "latitude": 51.089091, "longitude": 3.606198 },
            { "latitude": 51.110157, "longitude": 3.869108 },
            { "latitude": 50.995344, "longitude": 3.878717 },
            { "latitude": 50.995685, "longitude": 3.628888 }
        ]
    }

 ]

 const coordinates = {
    "latitude": 51.2194,
    "longitude": 4.4025
}
****************************************************************************/
const geolib = require('geolib')

async function execute () {
  const { coordinates, geofences } = options.requiredProperties.geofences
  let parsedCoordinates, parsedGeofences
  try {
    parsedCoordinates = typeof coordinates === 'string' ? JSON.parse(coordinates) : coordinates
    parsedGeofences = typeof geofences === 'string' ? JSON.parse(geofences) : geofences
  } catch (error) {
    throw new Error('Provided properties can\'t be parsed', error)
  }

  const results = parsedGeofences.map(geofence => checkGeofence(geofence, parsedCoordinates))

  send(null, {
    observedState: results.some(result => result.inside) ? 'IN' : 'OUT',
    rawData: {
      device: { parsedCoordinates },
      results: results
    }
  })
}

function checkGeofence (geofence, coordinates) {
  let inside = null

  if (geofence.type === 'circle') {
    inside = geolib.isPointWithinRadius(coordinates, geofence.coordinates, geofence.radius)
  } else if (geofence.type === 'polygon') {
    inside = geolib.isPointInPolygon(coordinates, geofence.coordinates)
  } else {
    throw new Error('Wrong geofence provided')
  }

  return {
    geofence,
    inside
  }
}

execute().catch(send)
