const axios = require('axios')

const { token, user, message, title } = options.requiredProperties

async function execute () {
  if (!token || !user || !message || !title) {
    return send(new Error('Missing Property'))
  }
  try {
    const response = await axios.post(
      'https://api.pushover.net/1/messages.json',
      { token, user, message: options.requiredProperties.message, title }
    )
    send(null, {
      observedState: 'Success', rawData: { response }
    })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Calling pushover failed: ' + error } })
  }
}

execute()
