script:
const { query } = options.requiredProperties

async function execute () {
  if (!query) {
    return send(new Error('Missing property: query'))
  }
  let parsedQuery
  try {
    parsedQuery = typeof query === 'string' ? JSON.parse(query) : query
  } catch (error) {
    return send(new Error('Failed to parse query: ' + error))
  }

  try {
    const response = await waylay.resources.search(parsedQuery)
    send(null, {
      observedState: 'Collected',
      rawData: { resources: response.values }
    })
  } catch (error) {
    send(error)
  }
}

execute()
