const { script } = options.requiredProperties

async function execute () {
  try {
    const result = await eval(`(${script})()`)
    if (typeof result !== 'object' || result === null) {
      return send(new Error('return type of script is not an object'))
    }

    send(null, { observedState: 'Success', rawData: result })
  } catch (error) {
    send(error)
  }
}

execute()
