const axios = require('axios')
const { channel, text } = options.requiredProperties

async function execute () {
  let accessToken
  try {
    const hoardResp = await waylay.hoard.getTokens('slack', { scope: 'organisation' })
    accessToken = hoardResp.bot.bot_access_token
  } catch (error) {
    console.error(error)
    return send(null, {
      observedState: 'Error',
      rawData: { errorMessage: 'Error fetching token from hoard: ' + error.response.data.message }
    })
  }

  if (!accessToken || !channel || !text) {
    return send(new Error('Missing property'))
  }
  try {
    const response = await axios.post('https://slack.com/api/chat.postMessage',
      { text, channel },
      { headers: { Authorization: `Bearer ${accessToken}` } })
    send(null, { observedState: 'Success', rawData: { response: response.data  } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Error posting to slack api: ' + error } })
  }
}

execute()
