const handlebars = require('handlebars')

async function execute () {
  handlebars.registerHelper('TASK_ID', () => {
    return options.task.TASK_ID
  })

  const template = options.requiredProperties.template
  const context = options.rawData

  const compiledTemplate = handlebars.compile(template)

  send(null, {
    observedState: 'Success',
    rawData: { result: compiledTemplate(context) }
  })
}

execute().catch(send)
