script:
const transformer = options.requiredProperties.transformer

try {
    if (!transformer) {
        throw new Error('Provide a valid transformer')
    }

    const result = eval(`(${transformer})`)

    if (typeof result !== 'object' || result === null) {
        throw new Error('Expected result to be an object')
    }

    send(null, { rawData: result, observedState: 'Success' })
} catch (error) {
    console.log(error)
    send(null, { observedState: 'Error' })
}
