const { alarmId, severity, status } = options.requiredProperties

async function execute () {
  if (!alarmId) {
    return send(new Error('Missing property'))
  }
  try {
    const response = await waylay.alarms.update(alarmId, { severity, status })
    send(null, { observedState: 'Success', rawData: { response } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Updating alarm failed: ' + error } })
  }
}

execute()
