const jsforce = require('jsforce')
const pify = require('pify')
const _ = require('lodash')

async function main () {
  const caseObject = cleanObject({
    Id: options.requiredProperties.id,
    Subject: options.requiredProperties.subject,
    Description: options.requiredProperties.description,
    Status: options.requiredProperties.status,
    Priority: options.requiredProperties.priority,
    AssetId: options.requiredProperties.assetId,
    AccountId: options.requiredProperties.accountId,
    Reason: options.requiredProperties.reason,
    OwnerId: options.requiredProperties.ownerId,
    Product__c: options.requiredProperties.product__c
  })

  const comment = options.requiredProperties.comment

  const conn = await getSalesforceConnection()

  const SfCaseComment = pify(conn.sobject('CaseComment'))
  const SfCase = pify(conn.sobject('Case'))

  let res

  if (comment) {
    console.log('add only comment')
    res = await SfCaseComment.insert({ CommentBody: comment, ParentId: caseObject.Id })
  } else {
    res = await SfCase.update(caseObject)
  }

  send(null, { observedState: 'Success', rawData: { message: JSON.stringify(res) } })
}

async function getSalesforceConnection () {
  try {
    const { access_token: accessToken } = await waylay.hoard.getTokens('salesforce', { scope: 'organisation' })

    if (!accessToken) {
      throw new Error('No user or token provided via Hoard service')
    }

    const jsclient = new jsforce.Connection({
      instanceUrl: 'https://eu26.salesforce.com',
      accessToken
    })

    return jsclient
  } catch (error) {
    throw new Error(`error: ${error} \n possible solution: Connect Salesforce through the connections`)
  }
}

/**
 * remove empty values in object before sending to salesforce
 */
function cleanObject (object) {
  return _.reduce(object, (acc, v, k) => {
    if (v) acc[k] = v
    return acc
  }, {})
}

main().catch(send)
