const Vonage = require('@vonage/server-sdk')
const pify = require('pify')

const { VONAGE_KEY, VONAGE_SECRET, message, to, from = 'Waylay' } = options.requiredProperties

async function execute () {
  if (!VONAGE_SECRET || !message || !VONAGE_KEY || !to) {
    return send(new Error('Missing property'))
  }

  const vonage = new Vonage({
    apiKey: VONAGE_KEY,
    apiSecret: VONAGE_SECRET
  })
  const sendSms = pify(
    vonage.message.sendSms.bind(vonage.message)
  )

  try {
    const responseData = await sendSms(from, to, message)
    if (responseData.messages[0].status === '0') {
      console.log('Message sent successfully.')
      send(null, { observedState: 'Success', rawData: { response: responseData } })
    } else {
      console.error(`Message failed with error: ${responseData.messages[0]['error-text']}`)
      send(null, {
        observedState: 'Error',
        rawData: { errorMessage: `Message failed with error: ${responseData.messages[0]['error-text']}` }
      })
    }
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Sending message failed: ' + error } })
  }
}

execute()
