const axios = require('axios')

const { ZENDESK_USER: username, ZENDESK_KEY: token, ZENDESK_DOMAIN: domain, subject, message, requester } = options.requiredProperties

async function execute () {
  if (!username || !token || !subject || !message || !domain || !requester) {
    return send(new Error('Missing Property'))
  }
  const url = 'https://' + domain + '.zendesk.com/api/v2/tickets.json'
  const data = {
    ticket: {
      requester: {
        name: requester
      },
      subject: subject,
      comment: message
    }
  }
  const auth = {
    username: username + '/token',
    password: token
  }
  try {
    const response = await axios.post(url, data, { auth })
    send(null, { observedState: 'Success', rawData: { response: response.data } })
  } catch (error) {
    console.error(error)
    send(null, { observedState: 'Error', rawData: { errorMessage: 'Failed to create ticket: ' + error } })
  }
}

execute()
